import React from 'react';
import click from './assets/clickSound.mp3';
import drag from './assets/dragSound.mp3';


export function clickSound() {
    const audio = new Audio(click);
    audio.volume = 0.2; 
    audio.currentTime = 0.05; 
    audio.play();
    setTimeout(() => {
        audio.pause();
        audio.currentTime = 0;
    }, 500); 
}

export function dragSound() {
    const audio = new Audio(drag);
    audio.volume = 0.1;
    audio.play();
    setTimeout(() => {
        audio.pause();
        audio.currentTime = 0;
    }, 500); 
}
