import React from 'react';
import sound from './assets/sound.mp3';

export function click() {
    const audio = new Audio(sound);
    audio.currentTime = 2; // start at 2 seconds
    audio.play();
    setTimeout(() => {
        audio.pause();
        audio.currentTime = 0;
    }, 500); // play for 0.5 seconds (500 ms)
}
