import React, { useState } from 'react';
import './Settings.css';
import { AwesomeButton } from 'react-awesome-button';
import 'react-awesome-button/dist/styles.css';

const Settings = ({ onStart }) => {
  const [mode, setMode] = useState('time');
  const [operations, setOperations] = useState({
    addition: true,
    subtraction: true,
    multiplication: true,
    division: true,
  });
  const [timeLimit, setTimeLimit] = useState(10); // Default time
  const [questionCount, setQuestionCount] = useState(10); // Default question count
  const [maxDigits, setMaxDigits] = useState(2); // Default max digits

  const handleOperationChange = (operation) => {
    setOperations((prev) => ({
      ...prev,
      [operation]: !prev[operation],
    }));
  };

  const isAtLeastOneOperationSelected = Object.values(operations).some(Boolean);

  return (
    <div>
      <header className ='settingsHeader'>
        MATH MANIA
      </header>
      <div>
        <h3>Select Game Mode</h3>
        <label>
          <input
            type="radio"
            value="time"
            checked={mode === 'time'}
            onChange={() => setMode('time')}
          />
          Time-Based Mode
        </label>
        <label>
          <input
            type="radio"
            value="questions"
            checked={mode === 'questions'}
            onChange={() => setMode('questions')}
          />
          Questions-Based Mode
        </label>
      </div>

      {mode === 'time' && (
        <div>
          <h3>Customize Time Limit (10 - 60 seconds)</h3>
          <input
            type="range"
            min="10"
            max="60"
            value={timeLimit}
            onChange={(e) => setTimeLimit(Number(e.target.value))}
          />
          <p>{timeLimit} seconds</p>
        </div>
      )}

      {mode === 'questions' && (
        <div>
          <h3>Customize Number of Questions (5 - 50)</h3>
          <input
            type="range"
            min="5"
            max="50"
            value={questionCount}
            onChange={(e) => setQuestionCount(Number(e.target.value))}
          />
          <p>{questionCount} questions</p>
        </div>
      )}

      <div>
        <h3>Maximum Number of Digits (1 - 5)</h3>
        <input
          type="range"
          min="1"
          max="5"
          value={maxDigits}
          onChange={(e) => setMaxDigits(Number(e.target.value))}
        />
        <p>Maximum digits: {maxDigits}</p>
      </div>

      <div>
        <h3>Select Operations</h3>
        <label>
          <input
            type="checkbox"
            checked={operations.addition}
            onChange={() => handleOperationChange('addition')}
          />
          Addition (+)
        </label>
        <label>
          <input
            type="checkbox"
            checked={operations.subtraction}
            onChange={() => handleOperationChange('subtraction')}
          />
          Subtraction (-)
        </label>
        <label>
          <input
            type="checkbox"
            checked={operations.multiplication}
            onChange={() => handleOperationChange('multiplication')}
          />
          Multiplication (ร)
        </label>
        <label>
          <input
            type="checkbox"
            checked={operations.division}
            onChange={() => handleOperationChange('division')}
          />
          Division (รท)
        </label>
      </div>

      <button
        onClick={() =>
          onStart(mode, operations, timeLimit, questionCount, maxDigits)
        }
        disabled={!isAtLeastOneOperationSelected}
      >
        Start Game
      </button>
    </div>
  );
};

export default Settings;
